package com.nakedquasar.gamecenter.mvc.dto;

import java.util.Date;
import java.util.UUID;

import javax.validation.constraints.NotNull;
import javax.validation.constraints.Size;

import org.springframework.format.annotation.DateTimeFormat;
import org.springframework.web.multipart.MultipartFile;

import com.nakedquasar.gamecenter.mvc.utils.ImageUtils;

public class PlayerDto {	
	private UUID playerId;
	@NotNull
	@Size(min = 1, max = 255)
	private String playerUsername;
	private String playerPassword;
	@NotNull
	@Size(min = 1, max = 255)
	private String playerDisplayName;
	private String playerFirstName;
	private String playerLastName;
	private String playerEmail;
	@DateTimeFormat (pattern="yyyy-mm-dd")
	private Date playerBirthdate;
	private String playerSex;
	private String playerRole;
	@NotNull
	@Size(min = 2, max = 255)
	private String playerPlatform;
	private String playerPicture="";
	private MultipartFile playerPictureRaw;
	@NotNull
	private boolean playerEnabled;
	private Date dateTimeOfCreation;	
	private String playerPlatformImg = "";
	private int leaderboardsCount;
	private int achievementsCount;
	private int logsCount;
	private String firstLastName;
	private String ip;
	
	
	public UUID getPlayerId() {
		return playerId;
	}

	public void setPlayerId(UUID PlayerId) {
		this.playerId = PlayerId;
	}

	public String getPlayerUsername() {
		return playerUsername;
	}

	public void setPlayerUsername(String playerUsername) {
		this.playerUsername = playerUsername;
	}

	public String getPlayerPassword() {
		return playerPassword;
	}

	public void setPlayerPassword(String PlayerPassword) {
		this.playerPassword = PlayerPassword;
	}

	public String getPlayerFirstName() {
		return playerFirstName;
	}

	public void setPlayerFirstName(String PlayerFirstName) {
		this.playerFirstName = PlayerFirstName;
	}

	public String getPlayerLastName() {
		return playerLastName;
	}

	public void setPlayerLastName(String PlayerLastName) {
		this.playerLastName = PlayerLastName;
	}

	public Date getDateTimeOfCreation() {
		return dateTimeOfCreation;
	}

	public void setDateTimeOfCreation(Date dateTimeOfCreation) {
		this.dateTimeOfCreation = dateTimeOfCreation;
	}

	public boolean isPlayerEnabled() {
		return playerEnabled;
	}

	public void setPlayerEnabled(boolean playerEnabled) {
		this.playerEnabled = playerEnabled;
	}

	public String getPlayerPlatform() {
		return playerPlatform;
	}

	public void setPlayerPlatform(String playerPlatform) {
		this.playerPlatform = playerPlatform;
	}

	public String getPlayerRole() {
		return playerRole;
	}

	public void setPlayerRole(String playerRole) {
		this.playerRole = playerRole;
	}

	public String getPlayerDisplayName() {
		return playerDisplayName;
	}

	public void setPlayerDisplayName(String playerDisplayName) {
		this.playerDisplayName = playerDisplayName;
	}

	public String getPlayerEmail() {
		return playerEmail;
	}

	public void setPlayerEmail(String playerEmail) {
		this.playerEmail = playerEmail;
	}

	public Date getPlayerBirthdate() {
		return playerBirthdate;
	}

	public void setPlayerBirthdate(Date playerBirthdate) {
		this.playerBirthdate = playerBirthdate;
	}

	public String getPlayerSex() {
		return playerSex;
	}

	public void setPlayerSex(String playerSex) {
		this.playerSex = playerSex;
	}

	public String getPlayerPicture() {
		return playerPicture;
	}

	public void setPlayerPicture(String playerPicture) {
		this.playerPicture = ImageUtils.resize(playerPicture, 48, 48);
	}

	public String getPlayerPlatformImg() {
		if(playerPlatform.equals("MAC") || playerPlatform.equals("IPHONE")){
			return "";
		}
		
		if(playerPlatform.equals("PC") || playerPlatform.equals("WINDOWS_STORE_APP")|| playerPlatform.equals("WINDOWS_PHONE_8")){
			return "";
		}
		
		if(playerPlatform.equals("ANDROID")){
			return "";
		}
		
		if(playerPlatform.equals("WII")){
			return "";
		}
		
		if(playerPlatform.equals("PS3")){
			return "";
		}

		if(playerPlatform.equals("XBOX")){
			return "";
		}
		
		if(playerPlatform.equals("FLASH")){
			return "";
		}
		
		if(playerPlatform.equals("LINUX")){
			return "";
		}
		
		if(playerPlatform.equals("WEBPLAYER")){
			return "";
		}
		
		if(playerPlatform.equals("BLACKBERRY")){
			return "";
		}
		
		return playerPlatformImg;
	}

	public void setPlayerPlatformImg(String playerPlatformImg) {
		this.playerPlatformImg = playerPlatformImg;
	}

	public int getLeaderboardsCount() {
		return leaderboardsCount;
	}

	public void setLeaderboardsCount(int leaderboardsCount) {
		this.leaderboardsCount = leaderboardsCount;
	}

	public int getAchievementsCount() {
		return achievementsCount;
	}

	public void setAchievementsCount(int achievementsCount) {
		this.achievementsCount = achievementsCount;
	}

	public MultipartFile getPlayerPictureRaw() {
		return playerPictureRaw;
	}

	public void setPlayerPictureRaw(MultipartFile playerPictureRaw) {
		this.playerPictureRaw = playerPictureRaw;
	}

	public int getLogsCount() {
		return logsCount;
	}

	public void setLogsCount(int logsCount) {
		this.logsCount = logsCount;
	}

	public String getFirstLastName() {
		firstLastName = "";
		if(playerFirstName != null && !playerFirstName.isEmpty()){
			firstLastName+= playerFirstName;
		}
		
		if(playerLastName != null && !playerLastName.isEmpty()){
			firstLastName+= ("/"+playerLastName);
		}
		return firstLastName;
	}

	public void setFirstLastName(String firstLastName) {
		this.firstLastName = firstLastName;
	}

	public String getIp() {
		return ip;
	}

	public void setIp(String ip) {
		this.ip = ip;
	}
}
